refsection.programlisting:
x = sin(2*%pi*(0:4)/5);
y = cos(2*%pi*(0:4)/5);

plot2d(0,0,-1,"010"," ",[-1,-1,1,1])  // draw an empty bounded axes
e = xpoly(x, y)
set(e, "foreground", color("red"), "closed", "on");

refsection.scilab:image:
x = sin(2*%pi*(0:4)/5);
y = cos(2*%pi*(0:4)/5);
plot2d(0,0,-1,"010"," ",[-1,-1,1,1])
e = xpoly(x, y)
set(e, "foreground", color("red"), "closed", "on");
gcf().axes_size = [300 300];

refsection.programlisting:
// With marks instead of lines
plot2d(0,0,1,rect=[0,0,12,10], frameflag=3)
t=[0:4]*2*%pi/5;
x=5+5*cos(t);
y=5+5*sin(t);
e = xpoly(x,y,"marks");
set(e,"mark_style",2);

